package basics;

public class Swapping {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//before swap
		int a=10;
		int b=20;
		System.out.println("a : "+a+" "+"b :"+b);
		
		//after swap
		a=a+b;//10+20=30
		b=a-b;//30-20=10
		a=a-b;//30-10=20
		System.out.println("a : "+a+" "+"b :"+b);

		
	}
	

}
