package classroomwork;

public class MultiplicatnDoWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=1;
		System.out.println("Multiplication table of 24 is");
		do {
			System.out.println(24+ " * "+i+" : "+(24*i));
			i++;
		}
		while(i<=10) ;
		
		System.out.println();
		
		int j=1;
		System.out.println("Multiplication table of 50 is");
		do {
			System.out.println(50+ " * "+j+" : "+(50*j));
			j++;
		}
		while(j<=10) ;
		
		System.out.println();
		
		int k=1;
		System.out.println("Multiplication table of 29 is");
		do {
			System.out.println(29+ " * "+k+" : "+(29*k));
			k++;
		}
		while(k<=10) ;


	}
	
	
	
}
