package controlstatements;

public class DoWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//one time print
		int j=1;
		do {
			System.out.println(j);
			j++;
			}while (j>=5);//for 1 time print here j>=5
		
		System.out.println();
		
		int i=1;
		do {
			System.out.println(i);
			i++;
		}while (i<=5);//i<=5
		

	}

}
