package controlstatements;

public class ForLoop1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub	
		int n=1;
		for (int i=5;i>=n;i--) {
			for(int j=1;j<=i;j++) {
				System.out.print(i+"");
		}
		System.out.println();
		}
		
		int m=5;
		for (int i=1;i<=m;i++) {
			for(int j=1;j<=i;j++) {
				System.out.print(j+"");
		}
				System.out.println();
		}
		
	}

	}


